import React, { useState } from "react";
import NoteContext from "./noteContext";
const NoteState = (props)=>{
    const s1 = {
        "name":"vraj",
        "class":"6csbs"
    }
    const [state,setState] = useState(s1)
    update = () =>{
        setTimeout(()=>{
            setState({
                "name":"chirmi",
            "class":"8a"
        },1000  )
        })
    }
    return (
        <NoteContext.Provider value={s1}>
            {props.children}
        </NoteContext.Provider>
    )
}

export default NoteState;
