import { useState } from "react";
import NoteContext from "./noteContext";
const NoteState = (props) => {
    const host = "http://localhost:5000"
    const notesInitaial =[]

    const [notes, setNotes] = useState(notesInitaial)

    //get all a Note
    // console.log("Adding a new note")
    const getNotes =async () => {
        

    }

    //Add a Note
    // console.log("Adding a new note")
    const addNote =async (title, description, tag) => {
        const response = await fetch(`${host}/api/notes/allnote`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'auth-token': 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjYwMjhhMGUxZjJiNDJhOGQ1YjFlMTg1In0sImlhdCI6MTcxMTQ0MjQ0Nn0.GxB4B9cqgOdkgk2dUdOjq_vkfl0qqRfyWKTCxGlPC4Q'
            },
            body: JSON.stringify({title,description,tag})
        });
        let note = {
            "_id": notes._id,
            "user": "66028a0e1f2b42a8d5b1e185",
            "title": title,
            "description": description,
            "tag": tag,
            "data": "2024-03-26T08:56:09.262Z",
            "__v": 0
        };
        setNotes(notes.concat(note))
    }

    // Delete a Note
    const deleteNote = (id) => {
        console.log("Deleting the note with id" + id)
        const newNote = notes.filter((note) => {
            return note._id !== id
        })
        setNotes(newNote)
    }

    // Edit a note
    const editNote = async (id, title, description, tag) => {
        //api call

        const response = await fetch(`${host}/api/notes/updatenote/${id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
                'auth-token': 'eyJhbGciiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjYwMjhhMGUxZjJiNDJhOGQ1YjFlMTg1In0sImlhdCI6MTcxMTQ0MjQ0Nn0.GxB4B9cqgOdkgk2dUdOjq_vkfl0qqRfyWKTCxGlPC4Q'
            },
            body: JSON.stringify({title,description,tag})
        });
        const json = response.json();
        //login to edit in client
        for (let index = 0; index < notes.length; index++) {
            const element = notes[index]
            if (element._id === id) {
                element.title = title;
                element.description = description;
                element.tag = tag
            }
        }
    }

    return (
        <NoteContext.Provider value={{ notes, addNote, deleteNote, editNote,getNotes }}>
            {props.children}
        </NoteContext.Provider>
    )
}

export default NoteState;
