import React, { useContext, useEffect, useRef } from 'react'

import noteContext from "../context/notes/noteContext"
import NoteItem from './NoteItem';
import AddNote from './AddNote';
function Notes() {
    const context = useContext(noteContext);
    const { notes, getNotes } = context;
    // console.log(notes)
    useEffect(() => {
        getNotes()

    }, [])
    const [note,setNote] = useState({title:"",description:"",tag:"default"})
    const updateNote = (note) => {
        ref.current.click();
    }
    const handleClick= (e) =>{
        e.preventDefault();
        addNote(note.title,note.description,note.tag);
    }
    const onchange = (e)=>{
        setNote({...note,[e.target.name]:e.target.value})
    }
    const ref = useRef(null)
    return (
        <>
            <AddNote />
            <button type="button" class="btn btn-primary" ref={ref} data-bs-toggle="modal" data-bs-target="#exampleModal">
                Launch demo modal
            </button>
            <div className="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h1 className="modal-title fs-5" id="exampleModalLabel">Edit Note</h1>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body">
                            <form className='my-3'>
                                <div className="mb-3">
                                    <label htmlFor="title" className="form-label">Title</label>
                                    <input type="text" className="form-control" name="etitle" id="etitle" aria-describedby="emailHelp" onChange={onchange} />
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="description" className="form-label">Description</label>
                                    <input type="text" className="form-control" id="edescription" name="edescription" onChange={onchange} />
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="tag" className="form-label">Tag</label>
                                    <input type="text" className="form-control" id="etag" name="etag" onChange={onchange} />
                                </div>
                            </form>
                        </div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                            <button type="button" className="btn btn-primary">Update Note</button>
                        </div>
                    </div>
                </div>
            </div>
            <div className='container'>
                <div className="row">
                    <h2>Your Note</h2>
                    {notes.map((note, index) => {
                        return <NoteItem key={index} updateNote={updateNote} note={note} />;
                    })}
                </div>
            </div>
        </>
    )
}

export default Notes
